<?php//auto to arxeio emfanizei apla thn forma dhmiourgias kainourgiou akinhtourequire_once("includes.php");check_valid_user();if(isset($_POST['newCat'])) $newCat=$_POST['newCat']; else $newCat=NULL;if(isset($_POST['category'])) $delCat=$_POST['category']; else $delCat=NULL;try{	if(filledOut($delCat))	{		dispHeader('Diagrafh kathgoriwn');		foreach($delCat as $cat)		{			echo "$cat <br />";			db_del_cat($cat);		}		dispFooter();	}	else if(filledOut($newCat))	{		$newCat=$_POST['newCat'];		db_insert1('categories','category',"'$newCat'");		dispHeader("Kainourgia kathgoria $newCat");		dispCategoriesSettings();		dispFooter();	}	else	{		dispHeader('Oi uparxouses kathgories');		dispCategoriesSettings();		dispFooter();	}}catch(Exception $e){	dispHeader("Error:");	echo $e->getMessage();	dispFooter();	exit;}      ?>