<?phprequire_once("includes.php");check_valid_user();if(isset($_POST['newFac'])) $newFac=$_POST['newFac']; else $newFac=NULL;if(isset($_POST['facility'])) $delFac=$_POST['facility']; else $delFac=NULL;try{	if(filledOut($delFac))	{		dispHeader('Diagrafh paroxwn');		foreach($delFac as $fac)		{			echo "$fac <br />";			db_del_fac($fac);		}		dispFooter();	}	else if(filledOut($newFac))	{		$newFac=$_POST['newFac'];		db_insert1('facilities','facility',"'$newFac'");		dispHeader("Kainourgia paroxh $newFac");		dispFacilitiesSettings();		dispFooter();	}	else	{		dispHeader('Oi uparxouses paroxes');		dispFacilitiesSettings();		dispFooter();	}}catch(Exception $e){	dispHeader("Error:");	echo $e->getMessage();	dispFooter();	exit;}      ?>